/**
 * @file Postをローカルに保存するユーティリティ
 */
import Lockr from 'lockr'
import { IQiitaPost } from '@/types/qiita'

enum LocalStorageKey {
  SAVED_POSTS = 'K1'
}

const OFFLINE_IMAGE = 'data:image/png;base64,'

class PostStorage {
  constructor (
    private prefix = 'qov__'
  ) {
    Lockr.prefix = this.prefix
  }

  /**
   * 保存されたポストを参照する
   */
  public get savedPosts (): Record<string, IQiitaPost> {
    return Lockr.get(LocalStorageKey.SAVED_POSTS, {})
  }

  /**
   * Qiitaポストを保存する
   * @param post Qiitaポストオブジェクト
   */
  public async save (post: IQiitaPost): Promise<void> {
    post.rendered_body = await this.replaceImageUrlToBase64(post.rendered_body)

    Lockr.set(LocalStorageKey.SAVED_POSTS, {
      ...this.savedPosts,
      [post.id]: post
    })
  }

  /**
   * Qiitaポストをストレージからリストアする
   * @param id QiitaポストID
   */
  public restore (id: string): IQiitaPost | void {
    return this.savedPosts[id]
  }

  /**
   * ストレージからQiitaポストを削除する
   * @param id QiitaポストID
   */
  public delete (id: string): void {
    const savedPosts = JSON.parse(JSON.stringify(this.savedPosts))

    delete savedPosts[id]

    Lockr.set(LocalStorageKey.SAVED_POSTS, savedPosts)
  }

  /**
   * HTML中の画像URLをBase64に変換して埋め込む
   */
  private async replaceImageUrlToBase64 (html: string): Promise<string> {
    const $dummyElements = document.createElement('div')

    $dummyElements.innerHTML = html

    const $imgElements = $dummyElements.querySelectorAll('img[src]')

    for (let i = $imgElements.length - 1; i >= 0; i--) {
      const $imgElement = $imgElements.item(i)
      const src = $imgElement.getAttribute('src')

      if (!src) {
        continue
      }

      // TODO 実際に画像保存を実装する
      await Promise.resolve()

      $imgElement.setAttribute('src', OFFLINE_IMAGE)
      $imgElement.removeAttribute('srcset')
    }

    return $dummyElements.innerHTML
  }
}

const postStorage = new PostStorage()

export { PostStorage, postStorage }
